import React from 'react'
import { NavLink } from 'react-router-dom'
var classNames = require('classnames');
//MUI
import Container from 'muicss/lib/react/container';
import Row from 'muicss/lib/react/row';
import Col from 'muicss/lib/react/col';
import Button from 'muicss/lib/react/button';
//

const TextOverLay = (props) => {
    const { classes } = props.styles    
    const data = props.data

    //function manages to recieve three schemas of buttons display: primary (pri) or secondary(sec) or both(else statement)
    const buttonDisplay = (data) => {
        if (data.button.sec.text === '') {
            return (
                <span>
                    <NavLink to={data.button.pri.link}>
                        <Button color='primary' variant='contained' size='large' className={classes.TOLbutton}>{data.button.pri.text}</Button>
                    </NavLink>
                </span>
            )
        }
        else if (data.button.pri.text === '') {
            return (
                <span>
                    <NavLink to={data.button.sec.link}>
                        <Button color='secondary' variant='contained' size='large' className={classes.TOLbutton}>{data.button.sec.text}</Button>
                    </NavLink>
                </span>
            )
        } else {
            return (

                <span>
                    <NavLink to={data.button.pri.link}>
                        <Button
                            color='primary'
                            variant='contained'
                            size='large'
                            className={classes.TOLbutton}
                        >{data.button.pri.text}</Button>
                    </NavLink>

                    <NavLink to={data.button.sec.link}>
                        <Button
                            variant='contained'
                            color='secondary'
                            size='large'
                            className={classes.TOLbutton}
                        >{data.button.sec.text}</Button>
                    </NavLink>
                </span>
            )
        }
    }


    return (

        <div style={{ top: props.top }} className={classes.TOLtextRoot}>
            <div style={props.style}>
                <Container
                    data-wow-offset="50"
                    data-wow-delay="0.1s"
                    spacing={24}
                    alignItems={data.align}
                    direction='column'
                    container
                >

                    <Row item md={8} sm={12} xs={12}>
                        <div align={data.header.align} style={props.style} variant='h2' className={classes.TOLheader}>{data.header.text}</div>
                    </Row>

                    <Row item md={6} sm={12} xs={12}>
                        <Col container justify={data.paragraph.justify} direction='row'>
                            <Col item xl={8} md={8} lg={8} xs={8}>
                                <div style={props.style} className={classes.TOLparagraph, "mui--text-display1"} align={data.paragraph.align} >
                                    {data.paragraph.text}
                                </div>
                            </Col>
                        </Col>
                    </Row>

                    <Row item md={6} sm={12} xs={12}>
                        <Col container justify='flex-start' direction='row' alignItems='center'>
                            {buttonDisplay(data)}
                        </Col>
                    </Row>

                </Container>
            </div>
        </div>
    )
}


export default TextOverLay
